
def lvlcalc(str):
	lvl = 0
	for c in str:
		if(c=='('):
			lvl+=1
		if(c==')'):
			lvl-=1
	return lvl
	
def firstBasement(str):
	lvl = 0
	for i in range(len(str)):
		if(str[i]=='('):
			lvl+=1
		if(str[i]==')'):
			lvl-=1
		if(lvl == -1):
			return i+1
	return -1
	
